examples:
// Returns 404 if bucketlist is not found
GET /v2/bucketlists/1000/items/11
404

// Shows the details of a bucketlist item with valid params
GET /v2/bucketlists/1/items/11
200
{
  "item": {
    "id": 11,
    "name": "Subito vaco stips amet utroque.",
    "done": false,
    "date_created": "2015-11-30 01:18:24",
    "date_modified": "2015-11-30 01:18:24"
  }
}

// Returns 404 if item is not found
GET /v2/bucketlists/1/items/1000
404

// Shows the details of a bucketlist item with valid params
GET /v2/bucketlists/1/items/11
200
{
  "item": {
    "id": 11,
    "name": "Subito vaco stips amet utroque.",
    "done": false,
    "date_created": "2015-11-30 01:18:25",
    "date_modified": "2015-11-30 01:18:25"
  }
}

// Returns 401 if token is invalid
GET /v2/bucketlists/1/items/11
401
