examples:
// Does not create user with invalid email
POST /users
{
  "user": {
    "name": "Oscar",
    "email": "454@#(*)",
    "password": "tcpu1asg6zv"
  }
}
422
{
  "users": [
    "Email is invalid"
  ]
}

// Does not create user with empty email
POST /users
{
  "user": {
    "name": "Oscar",
    "email": "",
    "password": "fvbuxyd1sxx"
  }
}
422
{
  "users": [
    "Email can't be blank",
    "Email is invalid"
  ]
}

// Does not create user with short name
POST /users
{
  "user": {
    "name": "e",
    "email": "jarvis.schmeler@kerluke.com",
    "password": "2mhsaduqwa"
  }
}
422
{
  "users": [
    "Name is too short (minimum is 2 characters)"
  ]
}

// Does not create user with empty name
POST /users
{
  "user": {
    "name": "",
    "email": "email@email.com",
    "password": ""
  }
}
422
{
  "users": [
    "Password can't be blank",
    "Name can't be blank",
    "Name is too short (minimum is 2 characters)",
    "Email has already been taken"
  ]
}

// Does not create user with empty params
POST /users
{
  "user": {
    "name": "",
    "email": "",
    "password": ""
  }
}
422
{
  "users": [
    "Password can't be blank",
    "Name can't be blank",
    "Name is too short (minimum is 2 characters)",
